sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
//    if (isLoading || newValue === '') {
//       return;
//    }

   //Type appropriate comment here, and begin script below
	//clear what needed options
	var whatneeded = g_form.getValue('u_what_needed');
	g_form.clearOptions('u_what_needed');
	//get the requested type selected choice and based on that set choice list in what needed field
	var req_type = g_form.getValue('u_request_type');
	if(req_type == 'hr'){
		g_form.addOption('u_what_needed','hr1','Human Resources1');
		g_form.addOption('u_what_needed','hr2','Human Resources2');
		g_form.addOption('u_what_needed','other','Other');
	}
	if(req_type == 'legal'){
		g_form.addOption('u_what_needed','legal1','Legal1');
		g_form.addOption('u_what_needed','legal2','Legal2');
		g_form.addOption('u_what_needed','other','Other');
	}
	if(req_type == 'facilities'){
		g_form.addOption('u_what_needed','facilities1','Facilities1');
		g_form.addOption('u_what_needed','facilities2','Facilities2');
		g_form.addOption('u_what_needed','other','Other');
	}
	 // If the form is loading and it is not a new record, set the u_what_needed value to the
    // value from the record before it was loaded.requestype field is retaining its previous value
	//without this script
   if(isLoading && !g_form.isNewRecord()){
		g_form.setValue('u_what_needed', whatneeded);
		}
}
