sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
  if(!g_form.isNewRecord()){
	if (isLoading || newValue === '') {
      return;
   }
  }
   //Type appropriate comment here, and begin script below
	//Exending GlideAjax to retrieve email address using scriptinclude
	var ga = new GlideAjax('GetEmailAddress2');
	ga.addParam('sysparm_name','getEmail');
	ga.addParam('sysparm_userID',g_form.getValue('u_requested_for'));
	ga.getXML(populateEmailField);

	function populateEmailField(response){
		var email = response.responseXML.documentElement.getAttribute("answer");
		g_form.clearValue('u_requested_for_email');
		g_form.setValue('u_requested_for_email',email);
	}

}
