sys_script_include.script:
var GetEmailAddress2 = Class.create();


 GetEmailAddress2.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
// 	// Define the getEmail function.  
// 	// Create a GlideRecord for the User table.
// 	// Use the sysparm_userID passed from the client side to retrieve a record from the User table.
// 	// Return the email address for the requested record
	getEmail : function(){
		var user_Record = new GlideRecord("sys_user");
		//sysparm_indicates to the client script that a value is passed in throught he client script
		//at run time.use this.getParameter to retrieve parameter values
	user_Record.get(this.getParameter('sysparm_userID'));
		return user_Record.email+'';
	},
	type:'GetEmailAddress2'
 });
